import React, { useEffect, useState } from 'react';
import useGlobalStore from 'store/GlobalStore';
import { Carousel } from 'react-responsive-carousel';
import { TitleDivider } from 'components/shared';
import PromoCard from 'components/promotion/PromoCard/PromoCard';
import 'assets/scss/responsive-carousel-override.scss';
import './promo-carousels-container.scss';
const axios = require('axios').default;

export default function () {
  const [localSliderPercentage, setLocalSliderPercentage] = useState(100);
  const {
    offer: offerStore,
    provider: providerStore,
    pathway: pathwayStore,
  } = useGlobalStore();
  const[bannerImage,setBannerImage]= useState({})
  let Arr =[]
  //  console.log("------------",pathwayStore)

  const getBannerApi = async () => {
    // let token = JSON.parse(localStorage.getItem("currentSession"))
    // let user_id = token.id
    // let user_role = token.role
    let Data = await axios.post(`${process.env.REACT_APP_API_URL}/api/v1/files/get_bannner`)
    return Data
  }
  const offers = Object.values(offerStore.entities).map((o) => {
    return {
      ...o,
      entity_type: 'offer',
    };
  });

  const providers = Object.values(providerStore.entities).map((p) => {
    return {
      ...p,
      entity_type: 'provider',
    };
  });

  const pathways = Object.values(pathwayStore.entities).map((p) => {
    return {
      ...p,
      entity_type: 'pathway',
    };
  });

  const data = [...offers, ...pathways, ...providers];

  const localPromos = data.filter((d) => {
    return d.is_local_promo;
  });

  const mainPromos = data.filter((d) => {
    return d.is_main_promo;
  });

  useEffect(() => {
    const handleSliderPercentOnResize = () => {
      if (window.innerWidth >= 769) {
        setLocalSliderPercentage(34);
      } else {
        setLocalSliderPercentage(100);
      }
    };

    if (window.innerWidth >= 769) {
      setLocalSliderPercentage(34);
    } else {
      setLocalSliderPercentage(100);
    }

    window.addEventListener('resize', handleSliderPercentOnResize);

    return () =>
      window.removeEventListener('resize', handleSliderPercentOnResize);
  }, [localSliderPercentage]);

  useEffect(() => {
    getBannerApi().then(resp => {
      if(resp.status == 200){
         console.log(finalArr,'fina')
        
        // let bannerImage = {}
        let parseData 
        let finalArr = []
        let abc=[]
        setBannerImage(resp.data.data)
        resp.data.data.map(item => {
          item.landing_image.map(async (lt,i) => {
            parseData = await JSON.parse(lt)
            console.log("444444444",parseData)
             // console.log("lttttttttt",lt)
             await finalArr.push(parseData)
             console.log("qqqqqq",finalArr)
            //  finalArr.forEach(v=>{

            //       abc.push([v])
                  
            //  })
            //  setTimeout(()=>{
            //   console.log(abc,'abc')
            //  },2000)
            await setBannerImage(finalArr)

            // bannerImage.push(parseData)
            // finalArr.push(parseData)
          })
          // bannerImage = parseData
        })
      }
    }).catch(error => {
      console.log("errror", error)
    })
  }, [])
    // console.log("bannerImage=================================",mainPromos)

  console.log("bannerImage=================================",mainPromos)

  return (
    <div className="h-auto w-full">
      <Carousel
        className="custom-carousel promoCarousel mb-2 cursor-grab"
        centerMode
        infiniteLoop
        centerSlidePercentage={100}
        showArrows={true}
        showIndicators={false}
        swipeable={true}
        emulateTouch={true}
        showStatus={false}
        showThumbs={false}
        interval={7000}
        autoPlay={true}
        swipeScrollTolerance={1}
      >
        {mainPromos.map((promo, index) => {
          // console.log("pro", promo)
          return <PromoCard key={index} data={promo} banner={true} bannerImage={bannerImage}/>;
        })}
      </Carousel>
      <TitleDivider
        title={'LOCAL PROMOS'}
        align="center"
        classNames={{ middleSpan: 'text-base' }}
      />
      <Carousel
        className="custom-carousel promoCarousel mb-2 cursor-grab"
        centerMode
        infiniteLoop
        centerSlidePercentage={localSliderPercentage}
        showArrows={true}
        showIndicators={false}
        swipeable={true}
        emulateTouch={true}
        showStatus={false}
        showThumbs={false}
        interval={7000}
        autoPlay={true}
        swipeScrollTolerance={1}
      >
        {localPromos.map((promo, index) => {
          return (
            <PromoCard key={index} data={promo} size="small" className="mx-1" />
          );
        })}
      </Carousel>
    </div>
  );
}
