import React, { useState, useEffect } from 'react';
import {
  Layout,
  Form,
  Input,
  Row,
  Col,
  Select,
  InputNumber,
  Checkbox,
  Avatar
} from 'antd';
import { ImageUploadAndNameInputs } from 'components/shared';
import { PdfUploadFunction } from 'components/shared'
import { groupBy, property, isNil, remove, compact } from 'lodash';
import 'assets/scss/antd-overrides.scss';
const axios = require('axios').default;

const { Option } = Select;

const preloadOptions = (data = []) =>
  data.map((item, index) => {
    return (
      <Option value={item.id} key={index.toString()}>
        {item.name}
      </Option>
    );
  });

export default function OfferForm({
  datafields = [],
  providers = {},
  offers = [],
  offer = {},
  onChangeUpload,
  onChangeBannerUpload,
  bannerFile,
  file,
  userId = null,
  role,
  handlePropData
}) {
  const [getvalues,setGetValues]=useState()
  const getData = async () => {
    let pdfData = await axios.post(`http://localhost:8888/api/v1/files/get_generic`)
    return pdfData
  }

  const deleteData = async (item1,offer) => {
    console.log(offer,item1,"=============")
    let image = item1.original
    let user_id = offer.id
    let pdfData = await axios.post(`http://localhost:8888/api/v1/files/offer_delete_pdf`,{
      image,
      user_id
    })
    return pdfData
  }
  useEffect(() => {
    getData().then(resp => {
      console.log(resp)
      if(resp.status == 200){
        setGetValues(resp.data.data)
      }
    })
  },[])
  console.log("getvalues",getvalues)
  console.log("filterGeneric",offers)
  let filterGeneric = offers && offers.length && offers.filter(item => item.is_generic === true
  )
  const [getPdfUrl, setGetPdfUrl] = useState()
  const [hideFied, setHideFied] = useState(true)
  const handlePdfData = (getPdfUrl) => {
    setGetPdfUrl(getPdfUrl)
    handlePropData(getPdfUrl)
  }
  // console.log("pdf_link", pdf_link)
  providers = compact(Object.values(providers));
  datafields = Object.values(datafields);

  const grouped = groupBy(datafields, property('type'));
  const {
    offer_category = [],
    payment_unit = [],
    length_unit = [],
    credit_unit = [],
    topic = [],
    frequency_unit = [],
    cost_unit = [],
  } = grouped;
  let offerOptions = null;
  if (!isNil(offers) && offers.length) {
    const updatedOffers = remove(offers, (o) => {
      return !(o.id === offer.id);
    });
    offerOptions = preloadOptions(updatedOffers);
  }

  const handleType = (event) => {
    console.log("event",event)
    if (event.target.checked == true) {
      setHideFied(false)
    } else if(event.target.checked == false) {
      setHideFied(true)
    }
  }

  const handlePdfDelete = (item1,offer) => {
    console.log("iteeeeeee",item1,offer)
    deleteData(item1,offer).then(resp => {
      console.log("sucessssssss",resp)
    }).catch(error => {
      console.log("errro",error)
    })
  }
  console.log("role",role)
  return (
    <Layout>
      <ImageUploadAndNameInputs
        className="mb-2"
        userId={userId}
        onChangeUpload={onChangeUpload}
        onChangeBannerUpload={onChangeBannerUpload}
        file={file}
        bannerFile={bannerFile}
      >
        <div className="rubric_block">
          <Form.Item
            label="Rubric Attachment"
            name={'rubric_attachment'}>
            <PdfUploadFunction
              handlePdfData={handlePdfData} />
          </Form.Item>
          </div>
          {offer && offer !== null ?<div>
          {offer && offer.rubric_attachment && offer.rubric_attachment.length ? <p>
            {offer.rubric_attachment && offer.rubric_attachment.map(item => {
              let item1 = JSON.parse(item)
              return (
                <div>
                  <p onClick={() => handlePdfDelete(item1,offer)}>click</p>
                  <Avatar src="https://upload.wikimedia.org/wikipedia/commons/8/87/PDF_file_icon.svg" />
                  <p>{item1.name}</p>
                </div>)
            })}
          </p> : null}
        </div>: null}
        <Row gutter={8}>
          <Col
            className={role === 'provider' ? 'hidden' : ''}
            xs={24}
            sm={24}
            md={15}
          >
            <div className="flex flex-row">
              <Form.Item
                label="Provider"
                name="provider_id"
                labelAlign={'left'}
                colon={false}
                className="mb-0 inherit flex-col w-full"
              >
                <Select
                  className="custom-select-rounded-l-r-none"
                  showSearch
                  name="provider_id"
                >
                  {!isNil(providers) && providers.length
                    ? preloadOptions(providers)
                    : null}
                </Select>
              </Form.Item>
            </div>
          </Col>
          <Col
            span={9}
            xs={24}
            sm={24}
            md={role === 'provider' ? 10 : 9}
            className="media-margin-top"
          >
            <Form.Item
              label="Offer Type"
              name="category"
              labelAlign={'left'}
              colon={false}
              className="mb-0 inherit"
              rules={[
                { required: true, message: 'Please select a generic offer' },
              ]}
            >
              <Select className="rounded custom-select" name="category">
                {!isNil(offer_category) && offer_category.length
                  ? preloadOptions(offer_category)
                  : null}
              </Select>
            </Form.Item>
          </Col>
          {role === "admin" ?<Col xs={9} sm={24} md={8}>
            <Form.Item
              label="Add as Generic Offer"
              name="is_generic"
              colon={true}
              labelAlign={'left'}
              className="mt-2 mb-0 inherit"
              valuePropName="checked"
            >
              <Checkbox onChange={(event) => handleType(event)}
                defaultChecked={true}>Click here</Checkbox>
            </Form.Item>
          </Col> : null}
          {hideFied ? <Col
            span={9}
            xs={24}
            sm={24}
            md={role === 'provider' ? 10 : 9}
            className="media-margin-top"
          >
            <Form.Item
              label="Generic Offer"
              name="generic_type"
              labelAlign={'left'}
              colon={false}
              className="mb-0 inherit"
            >
              {role == "admin" ?<Select className="rounded custom-select" name="name">
                {!isNil(filterGeneric) && filterGeneric.length
                  ? preloadOptions(filterGeneric)
                  : null}
              </Select> : <Select className="rounded custom-select" name="name">
                {!isNil(getvalues) && getvalues.length
                  ? preloadOptions(getvalues)
                  : null}
              </Select>}
            </Form.Item>
          </Col> : null}
        </Row>
        <Row gutter={8}>
          <Col span={24}>
            <Form.Item
              label="Keywords"
              name="keywords"
              labelAlign={'left'}
              colon={false}
              className="mb-0 inherit"
            >
              <Input className="rounded" />
            </Form.Item>
          </Col>
        </Row>
      </ImageUploadAndNameInputs>
      <Col>
        <span className="text-gray-700 relative" style={{ bottom: 2 }}>
          Description
        </span>
        <Form.Item
          name="description"
          labelAlign={'left'}
          colon={false}
          className="inherit"
          rules={[{ required: true, message: 'Please provide a description' }]}
        >
          <Input.TextArea rows={4} className="rounded" />
        </Form.Item>
      </Col>
      <Row className="items-center mb-0">
        <span className="text-gray-700 relative" style={{ bottom: 2 }}>
          Related Offers
        </span>
        <Form.Item name="related_offers" className="w-full">
          <Select
            className="w-full rounded custom-select"
            showSearch
            mode="multiple"
          >
            {offerOptions}
          </Select>
        </Form.Item>
      </Row>
      <Row className="items-center mb-0">
        <span className="text-gray-700 relative" style={{ bottom: 2 }}>
          Prerequisites
        </span>
        <Form.Item name="prerequisites" className="w-full">
          <Select
            className="w-full rounded custom-select"
            showSearch
            mode="multiple"
          >
            {offerOptions}
          </Select>
        </Form.Item>
      </Row>
      <Row className="items-center mb-0">
        <span className="text-gray-700 relative" style={{ bottom: 2 }}>
          Topics
        </span>
        <Form.Item name="topics" className="w-full">
          <Select showSearch className="w-full custom-select" mode="multiple">
            {!isNil(topic) && topic.length ? preloadOptions(topic) : null}
          </Select>
        </Form.Item>
      </Row>
      <Row className="items-center mb-0 mt-2">
        <Col xs={24} sm={24} md={8}>
          <Form.Item
            label="location_type"
            name="location_type"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
            rules={[{ required: true, message: 'Please select an option' }]}
          >
            <Select name="location_type" className="custom-select">
              <Option value="online">Online</Option>
              <Option value="hybrid">Hybrid</Option>
              <Option value="in-person">In person</Option>
              <Option value="self-learning'">Self Learning</Option>
            </Select>
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={8}>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Learn/Earn"
            name="learn_and_earn"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
            rules={[{ required: true, message: 'Please select an option' }]}
          >
            <Select className="rounded custom-select">
              <Option value="learn">Learn</Option>
              <Option value="earn">Earn</Option>
              <Option value="both">Learn and Earn</Option>
            </Select>
          </Form.Item>
        </Col>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Frequency"
            name="frequency"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
            rules={[{ required: true, message: 'Please fill in this field' }]}
          >
            <InputNumber className="rounded w-full" />
          </Form.Item>
        </Col>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Frequency Unit"
            name="frequency_unit"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
            rules={[{ required: true, message: 'Please select an option' }]}
          >
            <Select className="rounded custom-select">
              {!isNil(frequency_unit) && frequency_unit.length
                ? preloadOptions(frequency_unit)
                : null}
            </Select>
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={8}>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Cost"
            name="cost"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
            rules={[{ required: true, message: 'Please fill in this field' }]}
          >
            <InputNumber className="rounded w-full" />
          </Form.Item>
        </Col>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Cost Unit"
            name="cost_unit"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
          >
            <Select className="rounded custom-select">
              {!isNil(cost_unit) && cost_unit.length
                ? preloadOptions(cost_unit)
                : null}
            </Select>
          </Form.Item>
        </Col>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Credit"
            name="credit"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
            rules={[{ required: true, message: 'Please fill in this field' }]}
          >
            <InputNumber className="rounded w-full" />
          </Form.Item>
        </Col>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Credit Unit"
            name="credit_unit"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
            rules={[{ required: true, message: 'Please select an option' }]}
          >
            <Select className="rounded custom-select">
              {!isNil(credit_unit) && credit_unit.length
                ? preloadOptions(credit_unit)
                : null}
            </Select>
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={8}>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Pay"
            name="pay"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
            rules={[{ required: true, message: 'Please fill in this field' }]}
          >
            <InputNumber className="rounded w-full" />
          </Form.Item>
        </Col>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Pay Unit"
            name="pay_unit"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
            rules={[{ required: true, message: 'Please select an option' }]}
          >
            <Select className="rounded custom-select">
              {!isNil(payment_unit) && payment_unit.length
                ? preloadOptions(payment_unit)
                : null}
            </Select>
          </Form.Item>
        </Col>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Length"
            name="length"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
          >
            <InputNumber className="rounded w-full" />
          </Form.Item>
        </Col>
        <Col xs={24} sm={24} md={6}>
          <Form.Item
            label="Length Unit"
            name="length_unit"
            labelAlign={'left'}
            colon={false}
            className="mb-0 inherit"
          >
            <Select className="rounded custom-select">
              {!isNil(length_unit) && length_unit.length
                ? preloadOptions(length_unit)
                : null}
            </Select>
          </Form.Item>
        </Col>
        <Col className="mt-2 mb-0" span={24}>
          <span className="text-gray-700 relative" style={{ bottom: 2 }}>
            External URL
          </span>
          <Form.Item
            name="external_url"
            labelAlign={'left'}
            colon={false}
            className="inherit mb-0"
          >
            <Input className="rounded" />
          </Form.Item>
        </Col>
        {(role === 'admin' && (
          <Row className="w-full relative" style={{ left: '0.4em' }}>
            <Col xs={24} sm={24} md={6}>
              <Form.Item
                name="is_local_promo"
                labelAlign={'left'}
                colon={false}
                className="mt-2 mb-0 inherit"
                valuePropName="checked"
              >
                <Checkbox defaultChecked={false}>Local Promo</Checkbox>
              </Form.Item>
            </Col>
            <Col xs={24} sm={24} md={6}>
              <Form.Item
                name="is_main_promo"
                labelAlign={'left'}
                colon={false}
                className="mt-2 mb-0 inherit"
                valuePropName="checked"
              >
                <Checkbox defaultChecked={false}>Main Promo</Checkbox>
              </Form.Item>
            </Col>
          </Row>
        )) ||
          null}
      </Row>
    </Layout>
  );
}
