import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons';
import { Button, Layout, Tooltip, Col } from 'antd';
import AuthService from 'services/AuthService';
import useGlobalStore from 'store/GlobalStore';
import axiosInstance from 'services/AxiosInstance';
import useAxios, { configure } from 'axios-hooks';
import { ToastContainer, toast } from 'react-toastify';
const axios = require('axios').default;
toast.configure()
const { Header } = Layout;

configure({
  axios: axiosInstance,
});
console.log("axiosInstance", axios)
export default function LogOutTopbar({ children, renderNextToLogOut }) {
  const { datafield } = useGlobalStore();
  const { entities, typeEqualsProvider } = datafield;
  const pdfData = Object.values(entities).filter(typeEqualsProvider);
  const [inputFile, setInputFile] = useState({
    files: []
  })
  const [getRole,setGetRole]= useState(
    JSON.parse(localStorage.getItem("currentSession"))
  )
  const pdfFileData = async (inputFile) => {
    let fileData = inputFile.files
    var bodyFormData = new FormData();
    await bodyFormData.append('files', fileData);
    let pdfData = await axios.post('http://localhost:8888/api/v1/files/upload_pdf_file', bodyFormData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
    return pdfData
  }

  const addPdfData = async (pdfItem) => {
    let pdf_link = pdfItem
    let token = JSON.parse(localStorage.getItem("currentSession"))
    let user_id = token.id
    let user_role = token.role
    let pdfData = await axios.post('http://localhost:8888/api/v1/files/addPdfData', {
      pdf_link,
      user_id,
      user_role
    })
    return pdfData
  }

  const onChangeHandler = (e) => {
    let emailvalue = { ...inputFile, files: e.target.files[0] }
    setInputFile(emailvalue)
  }

  const handleButton = async () => {
    await pdfFileData(inputFile).then(async resp => {
      if (resp.status === 200) {
        let pdfItem = resp.data.original
        if(pdfItem !== null){
          addPdfData(pdfItem).then(response => {
            console.log("ressssssssss",response)
            if(response.status === 200){
              notify("success")
              setInputFile({
                files: []
              })
            }
          }).catch(error => {
            notify("netork")
          })
        }
      } else {
        notify("netork")
      }
    }).catch(error => {
      notify("error")
    })
  }

  const notify = msg => {
    if (msg == "error") {
        toast.error("Please select the pdf format")
    } else if (msg == "success") {
        toast.success("Data Save Successfully")
    } else if(msg == "netork"){
      toast.network("Network error Please check again")
    }
}

var inputs = document.querySelectorAll('.file-input')

for (var i = 0, len = inputs.length; i < len; i++) {
  customInput(inputs[i])
}

function customInput (el) {
  const fileInput = el.querySelector('[type="file"]')
  const label = el.querySelector('[data-js-label]')
  
  fileInput.onchange =
  fileInput.onmouseout = function () {
    if (!fileInput.value) return
    
    var value = fileInput.value.replace(/^.*[\\\/]/, '')
    el.className += ' -chosen'
    label.innerText = value
  }
}

console.log("role",getRole)

  return (
    <Header className="px-6 bg-white h-12 flex items-center">
      <Col span={17}>{children}</Col>
      <Col span={7} className="flex justify-end items-center">
        {renderNextToLogOut}
        <div class='file-input'>
          <input type='file'  name="file"  onChange={(e) => onChangeHandler(e, "files")}/>
          <span class='button'>Choose</span>
          <span class='label' data-js-label><label>No file selected</label></span>
        </div>
       {!getRole.role === "provider" || getRole.role === "admin" ? 
        <button className="pdf_btn" onClick={() => handleButton()}>Upload PDF</button> :null}
        {getRole.role === "provider" || getRole.role === "admin" ? <Button type="link" onClick={() => AuthService.logout()}>
          <Tooltip title="Sign out">
            <FontAwesomeIcon className="text-black" icon={faSignOutAlt} />
          </Tooltip>
        </Button> :null }
      </Col>
    </Header>
  );
}
