import React, { useEffect, useState, useRef } from 'react';
import { Modal, Form, Table, Button, notification } from 'antd';
import useAxios, { configure } from 'axios-hooks';
import axiosInstance from 'services/AxiosInstance';
import useGlobalStore from 'store/GlobalStore';
import OfferForm from 'components/offer/OfferForm';
import dayjs from 'dayjs';
import moment from 'moment';
import AuthService from 'services/AuthService';
import UploaderService from 'services/Uploader';
import { compact, orderBy, head, groupBy, flow, mapValues } from 'lodash';
import { useImageAndBannerImage } from 'hooks';
import 'assets/scss/antd-overrides.scss';

configure({
  axios: axiosInstance,
});

const { Column } = Table;

export default function OfferUpdateModal({
  offer,
  onCancel,
  visible,
  offerStore,
  scopedToProvider = false,
  role,
}) {
  const formRef = useRef(null);
  const { id: userId, provider_id } = AuthService.currentSession;
  const [
    { file, newFile, onFileChange, setFile, onChangeFileUpload },
    {
      bannerFile,
      onBannerFileChange,
      newBannerFile,
      setBannerFile,
      onChangeBannerUpload,
    },
    reset,
  ] = useImageAndBannerImage();

  const {
    RelatedOffers = [],
    PrerequisiteOffers = [],
    DataFields = [],
    GroupsOfOffers: Pathways = [],
  } = offer;

  const [form] = Form.useForm();

  const [{ error: updateOfferError }, updateOffer] = useAxios(
    {
      method: 'PUT',
    },
    { manual: true }
  );

  const {
    datafield: datafieldStore,
    provider: providerStore,
  } = useGlobalStore();

  const submitUpdate = async () => {
    try {
      const values = await form.validateFields([
        'banner_image',
        'main_image',
        'generic_type',
        'is_generic',
        'rubric_attachment',
        'location_type',
        'category',
        'description',
        'learn_and_earn',
        'frequency',
        'frequency_unit',
        'cost',
        'cost_unit',
        'credit_unit',
        'pay_unit',
        'length',
        'length_unit',
        'name',
        'provider_id',
        'topics',
        'pay',
        'credit',
        'keywords',
        'related_offers',
        'prerequisites',
        'is_local_promo',
        'is_main_promo',
        'external_url',
      ]);

      const { data, status } = await updateOffer({
        url: `/offers/${offer.id}`,
        data: {
          ...values,
          updatedAt: new dayjs().toISOString(),
          'rubric_attachment': getUpdateValue,
          'banner_image': getBannerImage,
          'main_image': getMainImage,
        },
      });

      const fileable_type = 'offer';
      let filePayload = [];

      if (data && userId) {
        const offerEntity = offerStore.entities[data.id];
        filePayload = [...offerEntity.Files];

        if (onFileChange && newFile) {
          const results = await UploaderService.uploadFile(newFile, {
            uploaded_by_user_id: userId,
            fileable_type,
            fileable_id: data.id,
          });

          if (results && results.file.data) {
            filePayload.push({
              ...results.file.data,
            });
          }

          if (results.success) {
            notification.success({
              message: 'Success',
              description: 'Main image is uploaded',
            });
          }
        }

        if (onBannerFileChange && newBannerFile) {
          const results = await UploaderService.uploadFile(newBannerFile, {
            uploaded_by_user_id: userId,
            fileable_type,
            fileable_id: data.id,
            meta: 'banner-image',
          });

          if (results && results.file.data) {
            filePayload.push({
              ...results.file.data,
            });
          }

          if (results.success) {
            notification.success({
              message: 'Success',
              description: 'Banner image is uploaded',
            });
          }
        }
      }

      if (data) {
        offerStore.updateOne(data);
      }

      if (status === 200) {
        if (filePayload.length) {
          let clonedData = Object.assign(data);
          clonedData.Files = filePayload;
          offerStore.updateOne(clonedData);
        }
        notification.success({
          message: status,
          description: 'Successfully updated offer',
        });
        onCancel();
      }
    } catch (error) {
      console.error(error);
    }
  };

  function populateFields(o) {
    form.setFieldsValue({
      ...o,
      pay_unit: Number(o.pay_unit),
      length_unit: Number(o.length_unit),
      credit_unit: Number(o.credit_unit),
      frequency_unit: Number(o.frequency_unit),
      category: Number(o.category),
      cost_unit: Number(o.cost_unit),
      start_date: moment(o.start_date),
      topics: compact(
        DataFields.map(({ type, id }) => {
          if (type === 'topic') {
            return id;
          }
          return null;
        })
      ),
      related_offers: RelatedOffers.map(({ id }) => id),
      prerequisites: PrerequisiteOffers.map(({ id }) => id),
    });
  }

  useEffect(() => {
    if (updateOfferError) {
      const { status, statusText } = updateOfferError.request;
      notification.error({
        message: status,
        description: statusText,
      });
    }
    if (formRef.current) {
      populateFields(offer);
      saveDescription(offer);
    }
    if (offer.Files) {
      let groupedFiles = flow([
        (v) => v.filter((f) => f.fileable_type === 'offer'),
        (v) => groupBy(v, 'meta'),
        (v) =>
          mapValues(v, (f) =>
            orderBy(
              f,
              ['fileable_type', 'createdAt', 'id'],
              ['desc', 'desc', 'asc']
            )
          ),
      ])(offer.Files);

      if (!onFileChange && groupedFiles[null]) {
        setFile(head(groupedFiles[null]));
      }
      if (!onBannerFileChange && groupedFiles['banner-image']) {
        setBannerFile(head(groupedFiles['banner-image']));
      }
    }
  }, [offer, updateOfferError, formRef, file, bannerFile]);
  const [getPdfUrl, setGetPdfUrl] = useState()
  const [getUpdateValue, setGetUpdatedValue] = useState()
  const [getMainImage, setGetMainImage] = useState()
  const [getBannerImage, setGetBannerImage] = useState()

  const handlePropData = (getPdfUrl, getUpdateValue) => {
    setGetPdfUrl(getPdfUrl)
    setGetUpdatedValue(getUpdateValue)
    // setDeleteValue(getDeleteValue)
  }
  const handleUpadteMain = (getMainImage) => {
    setGetMainImage(getMainImage)
    // setDeleteValue(getDeleteValue)
  }
  const handleUpadteBanner = (getBannerImage) => {
    setGetBannerImage(getBannerImage)
    // setDeleteValue(getDeleteValue)
  }

  function saveDescription(p) {
    if (p && p.description) {

      setTimeout(function delay() {
        // setCount(count => count + 1);
        setDescriptionValue(p.description)
      }, 100);
      // handleDescriptionValue(p.description)
      // setDescriptionValue()
    }
  }
  return (
    <Modal
      destroyOnClose={true}
      forceRender={true}
      className="custom-modal"
      title={'Update Offer'}
      visible={visible}
      width={998}
      bodyStyle={{ backgroundColor: '#f0f2f5', padding: 0 }}
      footer={true}
      onCancel={onCancel}
      afterClose={() => {
        reset();
      }}
    >
      <Form form={form} ref={formRef}>
        <div className="p-6 overflow-y-auto" style={{ maxHeight: '32rem' }}>
          <OfferForm
            offers={Object.values(offerStore.entities)}
            datafields={datafieldStore.entities}
            providers={providerStore.entities}
            offer={offer}
            userId={userId}
            providerId={provider_id}
            onChangeUpload={onChangeFileUpload}
            file={file}
            scopedToProvider={scopedToProvider}
            role={role}
            bannerFile={bannerFile}
            onChangeBannerUpload={onChangeBannerUpload}
            handlePropData={handlePropData}
            handleUpadteMain={handleUpadteMain}
            handleUpadteBanner={handleUpadteBanner}
          />
          <section className="mt-2">
            <label className="mb-2 block">Pathways - Table</label>
            <Table
              dataSource={Pathways}
              className="ant-table-wrapper--responsive"
              rowClassName={() => 'antd-row'}
              rowKey="id"
            >
              <Column
                className="antd-col"
                title="ID"
                dataIndex="id"
                key="id"
                render={(text, record) => ({
                  children: text,
                  props: {
                    'data-title': 'ID',
                  },
                })}
              />
              <Column
                className="antd-col"
                title="Pathway Name"
                dataIndex="name"
                key="name"
                render={(text, record) => ({
                  children: text,
                  props: {
                    'data-title': 'Pathway Name',
                  },
                })}
              />
              <Column
                className="antd-col"
                title="Pathway Description"
                dataIndex="description"
                key="index"
                render={(text, record) => {
                  let children = 'N/A';
                  if (text.length) {
                    children = text;
                  }
                  return {
                    children: children,
                    props: {
                      'data-title': 'Pathway Description',
                    },
                  };
                }}
              />
            </Table>
          </section>
        </div>
        <section
          className="bg-white px-6 pt-5 pb-1 flex justify-center"
          style={{
            borderTop: '1px solid #f0f0f0',
          }}
        >
          <Button
            className="mr-3 px-10 rounded"
            size="small"
            type="primary"
            htmlType="submit"
            onClick={submitUpdate}
          >
            Update
          </Button>
          <Button
            className="px-10 rounded"
            size="small"
            type="dashed"
            onClick={() => onCancel()}
          >
            Cancel
          </Button>
        </section>
      </Form>
    </Modal>
  );
}
