import React from 'react';
import { Table, Button, Tag } from 'antd';
import 'assets/scss/antd-overrides.scss';
const axios = require('axios').default;

const { Column } = Table;

const ProviderButtons = ({ record, handleUpdateModal, viewEnrollments }) => {

  const postData = async (record) => {
    let id =record.id
    let is_display = record.is_display
    let pdfData = await axios.get(`http://localhost:8888/api/v1/files/upadte_show_status`,{
      id,is_display
    })
    return pdfData
  }

  const handleShowAdmin =(record) => {
    postData(record).then(resp => {
      console.log(resp,"resssssssss")
    }).catch(error => {
      console.log(error,"error")
    })
  }
  return (
    <>
      <Button
        type="default"
        className="mr-2 rounded"
        onClick={(e) => {
          e.stopPropagation();
          handleUpdateModal(record);
        }}
      >
        Update
      </Button>
      <Button
        className="rounded"
        type="default"
        onClick={() => viewEnrollments(record)}
      >
        View enrollments
      </Button>
      <p>{}</p>
      <Button type="checkbox" onClick={() => handleShowAdmin(record)}>check as a admin</Button>
    </>
  );
};

const StudentButtons = ({ record }) => {
  return (
    <Button type="default" className="mr-2 rounded" onClick={() => {}}>
      View My Class
    </Button>
  );
};

const ActionColumn = ({
  role,
  handleUpdateModal,
  viewEnrollments,
  handleEnrollOffer,
  handleShowAdmin
}) => {
  return (
    <Column
      className="antd-col"
      title="Actions"
      key="index"
      render={(text, record) => {
        return {
          children:
            role === 'student' ? (
              <StudentButtons record={record} />
            ) : (
              <ProviderButtons
                record={record}
                handleUpdateModal={handleUpdateModal}
                viewEnrollments={viewEnrollments}
                handleShowAdmin={handleShowAdmin}
              />
            ),
          props: {
            'data-title': 'Actions',
          },
        };
      }}
    />
  );
};

export default function OfferTable({
  data,
  providers,
  datafields,
  handleUpdateModal,
  handleRowSelection,
  viewEnrollments,
  role,
  handleShowAdmin
}) {
  const doHandleRowSelection =
    role === 'student'
      ? null
      : (record, rowIndex) => {
          return {
            onClick: (event) => {
              if (event.target.type === 'button') {
                return;
              }
              handleRowSelection(record, rowIndex);
            },
          };
        };

  return (
    <Table
      pagination={{ pageSize: 8 }}
      dataSource={data}
      bordered
      className="ant-table-wrapper--responsive ant-table-row-selectable"
      rowClassName={() => 'antd-row'}
      rowKey="id"
      onRow={doHandleRowSelection}
    >
       <Column
        className="antd-col"
        title="ID"
        dataIndex="id"
        key="id"
        render={(text, record) => ({
          children: text,
          props: {
            'data-title': 'ID',
          },
        })}
      />
      <Column
        className="antd-col"
        title="Name"
        dataIndex="name"
        key="name"
        render={(text, record) => ({
          children: text,
          props: {
            'data-title': 'Name',
          },
        })}
      />
       <Column
        className="antd-col"
        title="Location Type"
        dataIndex="location_type"
        key="location_type"
        render={(text, record) => ({
          children: text,
          props: {
            'data-title': 'location_type',
          },
        })}
      />
      <Column
        className="antd-col"
        title="Category"
        dataIndex="category"
        key="category"
        render={(id) => {
          let name = null;
          if (datafields[id]) {
            name = datafields[id].name;
          }
          return {
            children: name,
            props: { 'data-title': 'Category' },
          };
        }}
      />
      <Column
        className="antd-col"
        title="Provider"
        dataIndex="provider_id"
        key="provider_id"
        render={(id) => {
          let name = 'N/A';
          if (providers[id]) {
            name = providers[id].name;
          }
          return {
            children: name,
            props: { 'data-title': 'Provider' },
          };
        }}
      />
      <Column
        className="antd-col"
        title="Topics"
        dataIndex="DataFields"
        key="DataFields"
        render={(datafields = [], record) => {
          datafields = datafields.filter((d) => d.type === 'topic');
          let children = 'N/A';

          if (datafields.length) {
            children = (
              <>
                {datafields.map((datafield, index) => {
                  if (datafield.type !== 'topic') {
                    return null;
                  }
                  return (
                    <Tag
                      color={index % 2 ? 'blue' : 'orange'}
                      key={index.toString()}
                    >
                      {datafield.name}
                    </Tag>
                  );
                })}
              </>
            );
          }

          return {
            children,
            props: {
              'data-title': 'Topics',
            },
          };
        }}
      />
      {ActionColumn({
        handleUpdateModal,
        viewEnrollments,
        role,
        handleShowAdmin
      })}
      {/* <Button type="checkbox">Please check the user</Button> */}
    </Table>
  );
}
