            <header
              className={`py-1 px-2 text-center capitalize ${themeBackground}`}
              style={{
                borderTopRightRadius: '0.20rem',
                borderTopLeftRadius: '0.20rem',
              }}
            >
              {data && data.entity_type ? data.entity_type : ''}
            </header>
