              {(data && data.Files && data.Files.length && (
                <img
                  className="object-cover mx-auto"
                  style={{ height: 112 }}
                  src={
                    data.Files && data.Files.length
                      ? last(data.Files).file_link
                      : null
                  }
                  alt=""
                />
              )) || (
                  <div className="mx-auto p-6">
                    <FontAwesomeIcon
