                    if (data) {
                      togglePromo(data, 'local');
                    }
                  }}
                >
                  local
          </Button>
                <Popconfirm
                  title="Are you sure?"
                  onConfirm={confirm}
                  okText="Yes"
                  cancelText="No"
                >
                  <Button type="link" size="small" danger>
                    reset
            </Button>
                </Popconfirm>
              </footer>
            </div>
          </div>
      }
    </React.Fragment>
  );
}
