    //   if (onCurrentChange !== current) {
    //     setCurrentTopic(topics[index]);
    //   }
    // }, 50);
    // return null;
  };

  const handleCurrentItemOutlook = (current, total) => {
    const index = current - 1;
    // setTimeout(() => {
    //   if (onCurrentChange !== current) {
    //     setOnCurrentChange(current);
    //   }
    //   if (onCurrentChange !== current) {
    //     setCurrentTopic(outlook[index]);
    //   }
    // }, 50);
    // return null;
