import React, { useEffect, useState } from 'react';
import { Layout, Row, Col, Input, Form, Upload, message, Button } from 'antd';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCloudUploadAlt } from '@fortawesome/free-solid-svg-icons';
import 'assets/scss/antd-overrides.scss';
const axios = require('axios').default;

function PdfUploadFunction() {
  const [inputFile, setInputFile] = useState({
    files: []
  })
  const [pdfData,setPdfData] = useState()
  const onChangeUpload = (e) => {
    let emailvalue = { ...inputFile, files: e.target.files[0] }
    setInputFile(emailvalue)
  }
  const handleButton = async () => {
    await pdfFileData(inputFile).then(async resp => {
      console.log("resp",resp)
      if (resp.status === 200) {
        let pdfItem = resp.data.original
        setPdfData(pdfItem)
      }
    }).catch(error => {
      console.log(error,"erorrr")
    })
  }
  const pdfFileData = async (inputFile) => {
    let fileData = inputFile.files
    var bodyFormData = new FormData();
    await bodyFormData.append('files', fileData);
    let pdfData = await axios.post('http://localhost:8888/api/v1/files/upload_pdf_file', bodyFormData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
    return pdfData
  }
  console.log("inputFile",inputFile)
  return (
    <Layout className="h-auto mb-6">
       <Col span={7} className="flex justify-end items-center">
        <label>Rubric Attachment</label>
        <div class='file-input'>
          <input type='file'  name="file"  onChange={(e) => onChangeUpload(e, "files")}/>
          <span class='button'>Choose</span>
        <span class='label' data-js-label><label>{inputFile !== null ? inputFile.files.name : "Choose File"}</label></span>
        </div>
        <button className="pdf_btn" onClick={() => handleButton()}>Upload PDF</button> 
      </Col>
    </Layout>
  );
}

export default PdfUploadFunction;
